package hospital.management.system;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;public class update_patient_details extends JFrame {    update_patient_details(){        JPanel panel = new JPanel();        panel.setBounds(3,3,895,595);        panel.setBackground(new Color(109, 164, 170));        panel.setLayout(null);        add(panel);        ImageIcon imageIcon = new ImageIcon(ClassLoader.getSystemResource("icon/upd.png"));        Image image = imageIcon.getImage().getScaledInstance(350,350,Image.SCALE_DEFAULT);        ImageIcon imageIcon1 = new ImageIcon(image);        JLabel label = new JLabel(imageIcon1);        label.setBounds(500,150,400,400);        panel.add(label);        JLabel label1 = new JLabel("UPDATE PATIENT DETAILS");        label1.setBounds(300,10,500,100);        label1.setFont(new Font("Raleway",Font.BOLD,25));        label1.setForeground(Color.white);        panel.add(label1);        JLabel label2 = new JLabel("Name :");        label2.setBounds(80,150,150,20);        label2.setFont(new Font("Tahoma",Font.BOLD,16));        label2.setForeground(Color.white);        panel.add(label2);        Choice choice = new Choice();        choice.setBounds(260,150,200,25);        panel.add(choice);        try {            conn c= new conn();            ResultSet resultSet = c.statement.executeQuery("select * from Patient_Info");            while (resultSet.next()){                choice.add(resultSet.getString("Name"));            }        }catch (Exception e){            e.printStackTrace();        }        JLabel label3 = new JLabel("Room Number :");        label3.setBounds(80,200,150,20);        label3.setFont(new Font("Tahoma",Font.BOLD,16));        label3.setForeground(Color.white);        panel.add(label3);        JTextField textFieldR = new JTextField();        textFieldR.setBounds(260,200,200,25);        panel.add(textFieldR);        JLabel label4 = new JLabel("In-Time  :");        label4.setBounds(80,250,150,20);        label4.setFont(new Font("Tahoma",Font.BOLD,16));        label4.setForeground(Color.white);        panel.add(label4);        JTextField textFieldINTIme = new JTextField();        textFieldINTIme.setBounds(260,250,200,25);        panel.add(textFieldINTIme);        JLabel label5 = new JLabel("Amount Paid (Rs) :");        label5.setBounds(80,300,200,20);        label5.setFont(new Font("Tahoma",Font.BOLD,16));        label5.setForeground(Color.white);        panel.add(label5);        JTextField textFieldAmount = new JTextField();        textFieldAmount.setBounds(260,300,200,25);        panel.add(textFieldAmount);        JLabel label6 = new JLabel("Pending Amount (Rs) :");        label6.setBounds(80,350,200,20);        label6.setFont(new Font("Tahoma",Font.BOLD,16));        label6.setForeground(Color.white);        panel.add(label6);        JTextField textFieldPending = new JTextField();        textFieldPending.setBounds(260,350,200,25);        panel.add(textFieldPending);        JButton check = new JButton("CHECK");        check.setBounds(100,450,150,30);        check.setBackground(Color.black);        check.setForeground(Color.white);        panel.add(check);        check.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                String id = choice.getSelectedItem();                String q = "select * from Patient_Info where Name = '"+id+"'";                try{                    conn c = new conn();                    ResultSet resultSet = c.statement.executeQuery(q);                    while (resultSet.next()){                        textFieldR.setText(resultSet.getString("Room_Number"));                        textFieldINTIme.setText(resultSet.getString("Time"));                        textFieldAmount.setText(resultSet.getString("Deposite"));                    }                    ResultSet resultSet1 = c.statement.executeQuery("select* from room where room_no = '"+textFieldR.getText()+"'");                    while (resultSet1.next()){                        String price = resultSet1.getString("Price");                        int amountPaid = Integer.parseInt(price) - Integer.parseInt(textFieldAmount.getText());                        textFieldPending.setText(""+amountPaid);                    }                }catch (Exception E){                    E.printStackTrace();                }            }        });        JButton update = new JButton("UPDATE");        update.setBounds(280,450,150,30);        update.setBackground(Color.black);        update.setForeground(Color.white);        panel.add(update);        update.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                try {                    conn c = new conn();                    String q = choice.getSelectedItem();                    String room = textFieldR.getText();                    String time = textFieldINTIme.getText();                    String amount = textFieldAmount.getText();                    c.statement.executeUpdate("update Patient_Info set Room_Number = '"+room+"', Time = '"+time+"', Deposite = '"+amount+"' where name = '"+q+"'" );                    JOptionPane.showMessageDialog(null,"Updated Successfully");                    setVisible(false);                }catch (Exception E){                    E.printStackTrace();                }            }        });        JButton back = new JButton("BACK");        back.setBounds(190,500,150,30);        back.setBackground(Color.black);        back.setForeground(Color.white);        panel.add(back);        back.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                setVisible(false);            }        });        setUndecorated(true);        setSize(900, 600);        setLayout(null);        setLocation(300, 200);        setVisible(true);    }    public static void main(String[] args) {        new update_patient_details();    }}